import React, {Component} from 'react';

import './LogIn.css';

class LogIn extends Component {
    constructor() {
        super();
        this.state = {
            username : "",
            password : "",
            loggedIn : false
        }
        this.handleChange = this.handleChange.bind(this);
        this.authenticate = this.authenticate.bind(this);
    }

    handleChange(event) {
        const {name, value} = event.target;
        this.setState({
            [name]: value
        });
    }

    async authenticate() {
        const status = await fetch('/log_in/' + this.state.username);
        console.log(status)
        const json = await status.text(); // .json()
        console.log(json);
        // json is object with all info
        // because /log_in/ only returns username
        // /info would return everything

        // clear state
        this.setState({
            loggedIn : true
        });

        this.props.validate();
    }

    render() {
        if ( this.state.loggedIn ) {
            return(
                <div className = "LoginPage">
                    <h1 className = "LoginHeading">All done!</h1>
                </div>
            )
        }

        return(
            <div className = "LogInPage">
                <h1 className = "LogInHeading"> Log In to get InShape!</h1>
                <form className = "LoginFormBox">                    
                    <input 
                        type = "text"
                        name = "username"
                        placeholder = "Username"
                        onChange = {this.handleChange}
                        value = {this.state.username}
                        className = "LoginTextField" 
                        required
                    />
                    <br />
                    <input 
                        type = "password"
                        name = "password"
                        placeholder = "Password"
                        onChange = {this.handleChange}
                        value = {this.state.password}
                        className = "LoginTextField" 
                        required
                    />

                    <div>
                        <center><div onClick = {this.authenticate} className = "LoginSubmitButton">Log In</div></center>
                    </div>  
                </form>
            </div>
        )
    }
}

export default LogIn;
